import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent implements OnInit {

  foods: Object[] =[];
  myList: { name: string, calories: number, quantity: number, image: string }[] = [];
  pattern: string="";
  isEditing: boolean = false;
  newFoodName: string = "Example Name";
  newFoodCalories: number = 250;
  newFoodImage: string = "https://imagefinder.co/storage/w1000/images/2019/02/freestocks_sandwich_3-1000x667.jpg";
  quantity: number=0;
  totalCalories: number = 0;


  constructor() { }

  ngOnInit(): void {
    this.foods = this.foods;
  }

  enableUserToAddFood() {
    this.isEditing = !this.isEditing;
  }

  newFood() {
    const newFood = {
      name: this.newFoodName,
      calories: this.newFoodCalories,
      image: this.newFoodImage,
      quantity: 0
    }

    this.foods.unshift(newFood);

    this.isEditing = true;
    this.newFoodName = "";
    this.newFoodCalories;
    this.newFoodImage = "";
  }

  addToMyList(food: any, quantityInput: any) {
    const existingFood = this.myList.find(item => item.name === food.name)
    const quantity = Number(quantityInput.value)

    if (existingFood) {
      existingFood.quantity += quantity;
    } else {
      food.quantity = quantity;
      this.myList.push(food);
    }
    this.totalCalories += (food.calories * quantity);
    this.quantity = 1;
  }
}
<div class="foods">
    <div id="searchBox">
        <div id="displayContainer">
        <label for="food-search">Search</label>
        <input id="searchBox" type="text" [(ngModel)]="pattern">
    </div></div>

    <div id="newFood">
        <button (click)="enableUserToAddFood()" class="new-food-button">Add New Food</button>
        <br>
        <div *ngIf="isEditing">
            <div id="name">
                <br>
                <label for="new-food-name">Name</label>
                <input type="text" [(ngModel)]="newFoodName">
                <br> <br>
            </div>
            <div id="calories">
                <label for="new-food-calories">Calories</label>
                <input type="text" [(ngModel)]="newFoodCalories">
                <br> <br>
            </div>
            <div id="image">
                <label for="new-food-name">Image</label>
                <input type="text" [(ngModel)]="newFoodImage">
                <br> <br>
            </div>
            <div id="displayContainer">
            <button (click)="newFood()">Add Food</button>
        </div></div>
    </div>

    <ul class="food-list">
        <li class="single-food" *ngIf="isEditing">
            <img [src]="newFoodImage">
            <h3 class="food-name">
                {{ newFoodName }}
                <small class="food-calories">{{ newFoodCalories }}</small>
            </h3>
        </li>



        <li class="single-food" *ngFor="let food of foods | foodSearch:'name':pattern;">
            <img [src]="food.image">
            <h3 class="food-name">
                {{ food.name }}
                <small class="food-calories">{{ food.calories }}</small>
            </h3>
            <input type="text" #quantity value="1">
            <button (click)="addToMyList(food, quantity)"> Add </button>
        </li>
    </ul>

</div>

<div class="my-food-list">
    <h2>Today's Foods <small>{{ totalCalories }} cal</small></h2>
    <ul>
        <li *ngFor="let food of myList">
            <span>{{ food.name }}</span>
            <span>{{ food.calories }}</span>
            <span *ngIf="food.quantity > 1">x{{ food.quantity }}</span>
        </li>
    </ul>
</div>
